#!/usr/bin/env python
import gi
gi.require_version('Gtk', '4.0')
gi.require_version('Adw', '1')
from gi.repository import Gtk, Adw, Gio, GLib
import subprocess
import os
import re

CONFIG_DIR = os.path.expanduser("~/.config/wireplumber/wireplumber.conf.d")
LUA_RULE_TEMPLATE = """
monitor.alsa.rules = [
  {{
    matches = [
      {{
        -- Rule for: {description}
        node.name = "{node_name}"
      }}
    ]
    actions = {{
      update-props = {{
        {properties}
      }}
    }}
  }}
]
"""

class AudioManager(Adw.Application):
    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.connect('activate', self.on_activate)
        self.win = None

    def on_activate(self, app):
        if not self.win:
            self.win = MainWindow(application=app)
        self.win.present()

class MainWindow(Gtk.ApplicationWindow):
    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.set_title("Audio Device Manager")
        self.set_default_size(600, 700)

        self.main_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL, spacing=10)
        self.main_box.set_margin_top(10)
        self.main_box.set_margin_bottom(10)
        self.main_box.set_margin_start(10)
        self.main_box.set_margin_end(10)
        self.set_child(self.main_box)

        header = Adw.HeaderBar()
        self.set_titlebar(header)

        # Refresh Button
        refresh_button = Gtk.Button(icon_name="view-refresh-symbolic")
        refresh_button.connect('clicked', self.refresh_lists)
        header.pack_start(refresh_button)

        self.sinks_box = self.create_device_section("Outputs (Sinks)")
        self.sources_box = self.create_device_section("Inputs (Sources)")

        self.main_box.append(self.sinks_box)
        self.main_box.append(self.sources_box)

        self.load_devices()

    def create_device_section(self, title):
        box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL, spacing=6)
        label = Gtk.Label(xalign=0)
        label.set_markup(f"<b>{title}</b>")
        box.append(label)

        scrolled_window = Gtk.ScrolledWindow()
        scrolled_window.set_policy(Gtk.PolicyType.NEVER, Gtk.PolicyType.AUTOMATIC)
        scrolled_window.set_min_content_height(250)

        listbox = Gtk.ListBox()
        listbox.set_selection_mode(Gtk.SelectionMode.SINGLE)
        listbox.connect("row-selected", self.on_row_selected)

        scrolled_window.set_child(listbox)
        box.append(scrolled_window)

        # Details Box
        details_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL, spacing=6)
        details_box.set_visible(False)
        box.append(details_box)

        box.listbox = listbox
        box.details_box = details_box

        return box

    def load_devices(self):
        self.sinks_box.listbox.foreach(lambda child: self.sinks_box.listbox.remove(child))
        self.sources_box.listbox.foreach(lambda child: self.sources_box.listbox.remove(child))

        self.sinks_box.details_box.set_visible(False)
        self.sources_box.details_box.set_visible(False)

        try:
            result = subprocess.run(['wpctl', 'status'], capture_output=True, text=True, check=True)
            self.parse_wpctl_status(result.stdout)
        except (subprocess.CalledProcessError, FileNotFoundError) as e:
            self.show_error("Failed to get device list", str(e))

    def parse_wpctl_status(self, output):
        lines = output.split('\\n')
        current_section = None

        for line in lines:
            line = line.strip()
            if line.startswith('├─ Sinks:'):
                current_section = 'sinks'
                continue
            elif line.startswith('├─ Sources:'):
                current_section = 'sources'
                continue
            elif line.startswith('└─') or line.startswith('│  └─'):
                 current_section = None
                 continue

            if current_section in ['sinks', 'sources']:
                match = re.match(r'(\*?\s*)(\d+)\.\s(.+)', line)
                if match:
                    is_default, device_id, description = match.groups()
                    self.add_device_row(current_section, device_id, description.strip())

    def add_device_row(self, section, device_id, description):
        listbox = self.sinks_box.listbox if section == 'sinks' else self.sources_box.listbox

        try:
            result = subprocess.run(['wpctl', 'inspect', device_id], capture_output=True, text=True, check=True)
            node_name = self.get_property(result.stdout, 'node.name')
            if not node_name:
                return # Skip devices without a node.name (e.g. device monitors)

            row = Gtk.ListBoxRow()
            row.device_id = device_id
            row.description = description
            row.node_name = node_name

            label = Gtk.Label(label=description, xalign=0)
            row.set_child(label)
            listbox.append(row)

        except (subprocess.CalledProcessError, FileNotFoundError):
            pass # Ignore inspect errors for now

    def on_row_selected(self, listbox, row):
        if not row:
            listbox.get_parent().get_parent().details_box.set_visible(False)
            return

        details_box = listbox.get_parent().get_parent().details_box
        # Clear previous widgets
        child = details_box.get_first_child()
        while child:
            details_box.remove(child)
            child = details_box.get_first_child()

        # New Name Entry
        entry_box = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL, spacing=6)
        entry_label = Gtk.Label(label="New Name:")
        name_entry = Gtk.Entry()
        name_entry.set_hexpand(True)
        # Use original name if available, otherwise current description
        original_name = self.get_original_name(row.node_name) or row.description
        name_entry.set_text(original_name)

        entry_box.append(entry_label)
        entry_box.append(name_entry)
        details_box.append(entry_box)

        # Buttons
        button_box = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL, spacing=6)
        button_box.set_halign(Gtk.Align.END)

        rename_button = Gtk.Button(label="Rename")
        rename_button.connect('clicked', self.on_rename_clicked, row, name_entry)

        hide_button = Gtk.Button(label="Hide")
        hide_button.connect('clicked', self.on_hide_clicked, row)

        reset_button = Gtk.Button(label="Reset")
        reset_button.connect('clicked', self.on_reset_clicked, row)

        button_box.append(rename_button)
        button_box.append(hide_button)
        button_box.append(reset_button)
        details_box.append(button_box)

        details_box.set_visible(True)

    def get_original_name(self, node_name):
        # A simple way to try and get the original name before our rules
        try:
            result = subprocess.run(['pactl', 'list'], capture_output=True, text=True, check=True)
            for line in result.stdout.split('\\n'):
                if node_name in line:
                    # Find the description in the block
                    block = result.stdout.split(line)[1].split('Sink #')[0].split('Source #')[0]
                    desc_match = re.search(r'Description:\s(.+)', block)
                    if desc_match:
                        return desc_match.group(1).strip()
        except Exception:
            return None
        return None

    def on_rename_clicked(self, button, row, entry):
        self.apply_rule(row, f'node.description = "{entry.get_text()}"')

    def on_hide_clicked(self, button, row):
        self.apply_rule(row, 'node.disabled = true')

    def on_reset_clicked(self, button, row):
        config_file = self.get_config_path(row.node_name)
        if os.path.exists(config_file):
            try:
                os.remove(config_file)
                self.restart_wireplumber()
            except OSError as e:
                self.show_error("Failed to remove config file", str(e))
        else:
            # If file doesn't exist, still refresh in case of manual changes
            self.restart_wireplumber()

    def apply_rule(self, row, properties_str):
        if not os.path.exists(CONFIG_DIR):
            os.makedirs(CONFIG_DIR)

        config_file = self.get_config_path(row.node_name)
        content = LUA_RULE_TEMPLATE.format(
            description=row.description,
            node_name=row.node_name,
            properties=properties_str
        )

        try:
            with open(config_file, 'w') as f:
                f.write(content)
            self.restart_wireplumber()
        except IOError as e:
            self.show_error("Failed to write config file", str(e))

    def get_config_path(self, node_name):
        # Create a safe filename from the node name
        safe_name = re.sub(r'[^a-zA-Z0-9_-]', '_', node_name)
        return os.path.join(CONFIG_DIR, f"99-custom-{safe_name}.conf")

    def restart_wireplumber(self):
        try:
            subprocess.run(['systemctl', '--user', 'restart', 'wireplumber.service'], check=True)
            # Give it a moment to restart before reloading
            GLib.timeout_add(500, self.load_devices)
        except (subprocess.CalledProcessError, FileNotFoundError) as e:
            self.show_error("Failed to restart WirePlumber", str(e))

    def refresh_lists(self, button):
        self.load_devices()

    def show_error(self, primary_text, secondary_text):
        dialog = Adw.MessageDialog.new(self,
                                       heading=primary_text,
                                       body=secondary_text)
        dialog.add_response("ok", "OK")
        dialog.connect("response", lambda d, r: d.close())
        dialog.present()


if __name__ == "__main__":
    app = AudioManager(application_id="com.example.audiomanager")
    app.run(None)

