#!/usr/bin/env python
import gi
gi.require_version('Gtk', '4.0')
gi.require_version('Adw', '1')
from gi.repository import Gtk, Adw, Gio, GLib
import subprocess
import os
import re

# Use a single, dedicated config file to avoid conflicts
CONFIG_FILE = os.path.expanduser("~/.config/wireplumber/wireplumber.conf.d/99-audio-manager-rules.conf")

class RuleManager:
    """Manages loading, saving, and applying all audio rules from a single file."""
    def __init__(self):
        self.rules = {}
        self.load_rules()

    def load_rules(self):
        self.rules = {}
        if not os.path.exists(CONFIG_FILE):
            return
        try:
            with open(CONFIG_FILE, 'r') as f:
                content = f.read()

            # Regex to find all rules in the file
            rule_matches = re.finditer(r'matches\s*=\s*\[\s*\{\s*\{\s*"node.name"\s*=\s*"(.*?)"\s*\}\s*\}\s*\]\s*actions\s*=\s*\{\s*update-props\s*=\s*\{(.*?)\}\s*\}', content, re.DOTALL)
            for match in rule_matches:
                node_name = match.group(1)
                props_str = match.group(2).strip()
                self.rules[node_name] = {}
                if 'node.disabled = true' in props_str:
                    self.rules[node_name]['disabled'] = True
                desc_match = re.search(r'node.description\s*=\s*"(.*?)"', props_str)
                if desc_match:
                    self.rules[node_name]['description'] = desc_match.group(1)
        except IOError:
            pass # Ignore read errors

    def set_rule(self, node_name, description=None, disabled=False):
        if node_name not in self.rules:
            self.rules[node_name] = {}

        if description is not None:
            self.rules[node_name]['description'] = description
            # A device can't be both renamed and disabled, rename takes precedence
            self.rules[node_name]['disabled'] = False
        elif disabled:
            self.rules[node_name]['disabled'] = True
            if 'description' in self.rules[node_name]:
                del self.rules[node_name]['description']

    def remove_rule(self, node_name):
        if node_name in self.rules:
            del self.rules[node_name]

    def save_and_apply(self):
        rule_definitions = []
        for node_name, props in self.rules.items():
            props_list = []
            if props.get('disabled'):
                props_list.append('node.disabled = true')
            elif props.get('description'):
                props_list.append(f'node.description = "{props["description"]}"')

            if props_list:
                rule_str = f"""  {{
    matches = [
      {{
        node.name = "{node_name}"
      }}
    ]
    actions = {{
      update-props = {{
        {', '.join(props_list)}
      }}
    }}
  }}"""
                rule_definitions.append(rule_str)

        if not rule_definitions:
            # If no rules, just delete the config file
            if os.path.exists(CONFIG_FILE):
                os.remove(CONFIG_FILE)
            return

        content = "monitor.alsa.rules = [\n" + ",\n".join(rule_definitions) + "\n]"

        config_dir = os.path.dirname(CONFIG_FILE)
        if not os.path.exists(config_dir):
            os.makedirs(config_dir)

        with open(CONFIG_FILE, 'w') as f:
            f.write(content)

class AudioManager(Adw.Application):
    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.connect('activate', self.on_activate)
        self.win = None

    def on_activate(self, app):
        if not self.win:
            self.win = MainWindow(application=app)
        self.win.present()

class MainWindow(Gtk.ApplicationWindow):
    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.set_title("Audio Device Manager")
        self.set_default_size(600, 700)
        self.rule_manager = RuleManager()

        self.main_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL, spacing=10)
        self.main_box.set_margin_top(10)
        self.main_box.set_margin_bottom(10)
        self.main_box.set_margin_start(10)
        self.main_box.set_margin_end(10)
        self.set_child(self.main_box)

        header = Adw.HeaderBar()
        self.set_titlebar(header)

        refresh_button = Gtk.Button(icon_name="view-refresh-symbolic")
        refresh_button.connect('clicked', self.refresh_lists)
        header.pack_start(refresh_button)

        self.sinks_box = self.create_device_section("Outputs (Sinks)")
        self.sources_box = self.create_device_section("Inputs (Sources)")

        self.main_box.append(self.sinks_box)
        self.main_box.append(self.sources_box)

        self.load_devices()

    def create_device_section(self, title):
        box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL, spacing=6)
        label = Gtk.Label(xalign=0)
        label.set_markup(f"<b>{title}</b>")
        box.append(label)

        scrolled_window = Gtk.ScrolledWindow()
        scrolled_window.set_policy(Gtk.PolicyType.NEVER, Gtk.PolicyType.AUTOMATIC)
        scrolled_window.set_min_content_height(250)

        listbox = Gtk.ListBox()
        listbox.set_selection_mode(Gtk.SelectionMode.SINGLE)
        listbox.connect("row-selected", self.on_row_selected)

        scrolled_window.set_child(listbox)
        box.append(scrolled_window)

        details_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL, spacing=6)
        details_box.set_visible(False)
        box.append(details_box)

        box.listbox = listbox
        box.details_box = details_box

        return box

    def load_devices(self):
        self.rule_manager.load_rules()
        self._clear_listbox(self.sinks_box.listbox)
        self._clear_listbox(self.sources_box.listbox)
        self.sinks_box.details_box.set_visible(False)
        self.sources_box.details_box.set_visible(False)

        try:
            result = subprocess.run(['wpctl', 'status'], capture_output=True, text=True, check=True)
            self.parse_wpctl_status(result.stdout)
        except (subprocess.CalledProcessError, FileNotFoundError) as e:
            self.show_error("Failed to get device list", str(e))

    def _clear_listbox(self, listbox):
        while (child := listbox.get_first_child()) is not None:
            listbox.remove(child)

    def parse_wpctl_status(self, output):
        # ... (The rest of the class remains largely the same)
        lines = output.split('\n')
        current_section = None

        for line in lines:
            if '├─ Sinks:' in line:
                current_section = 'sinks'
                continue
            elif '├─ Sources:' in line:
                current_section = 'sources'
                continue
            elif '└─' in line or 'Streams:' in line or 'Filters:' in line or 'Devices:' in line:
                current_section = None
                continue

            if current_section in ['sinks', 'sources']:
                match = re.search(r'(\d+)\.\s+(.+?)(?=\s+\[vol:|$)', line)
                if match:
                    device_id, description = match.groups()
                    self.add_device_row(current_section, device_id, description.strip())

    def _parse_property(self, output, prop_name):
        match = re.search(fr'\b{prop_name}\s*=\s*"(.*?)"', output)
        if match:
            return match.group(1)
        return None

    def add_device_row(self, section, device_id, current_description):
        listbox = self.sinks_box.listbox if section == 'sinks' else self.sources_box.listbox
        try:
            result = subprocess.run(['wpctl', 'inspect', device_id], capture_output=True, text=True, check=True)
            node_name = self._parse_property(result.stdout, 'node.name')
            if not node_name:
                return

            # Skip if rule says it's disabled
            if self.rule_manager.rules.get(node_name, {}).get('disabled', False):
                return

            row = Gtk.ListBoxRow()
            row.device_id = device_id
            row.node_name = node_name
            row.original_description = self._parse_property(result.stdout, 'device.description')

            label_text = self.rule_manager.rules.get(node_name, {}).get('description', current_description)
            label = Gtk.Label(label=label_text, xalign=0)
            row.set_child(label)
            listbox.append(row)
        except (subprocess.CalledProcessError, FileNotFoundError):
            pass

    def on_row_selected(self, listbox, row):
        if listbox is self.sinks_box.listbox:
            section_box = self.sinks_box
        else:
            section_box = self.sources_box

        details_box = section_box.details_box
        if not row:
            details_box.set_visible(False)
            return

        self._clear_listbox(details_box)

        name_entry = Gtk.Entry()
        name_entry.set_hexpand(True)
        current_custom_name = self.rule_manager.rules.get(row.node_name, {}).get('description')
        name_entry.set_text(current_custom_name or row.original_description or "")
        details_box.append(name_entry)

        button_box = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL, spacing=6)
